<!-- Content area --><div class="content"><!-- CKEditor default --><div class="panel panel-flat">    <div class="panel-heading">        <h5 class="panel-title">Add Question Detail</h5>    </div>    <div class="panel-body">        <form method="post" class="form-horizontal" action="<?php echo(base_url('admin/surveys/addQuestion/'.$surveyId)); ?>" enctype="multipart/form-data">            <input type="hidden" value="open" name="type">            <fieldset class="content-group">                <!--<legend class="text-bold">Add Team Detail</legend>-->                <div class="form-group">                    <label class="control-label col-lg-2">Title: </label>                    <div class="col-lg-6">                        <?php echo form_error('title'); ?>                        <input type="text" required="" name="title" value="<?php echo set_value('title'); ?>" class="form-control">                    </div>                </div>                <div class="form-group" id="cat">                    <label class="control-label col-lg-2">Category</label>                    <div class="col-lg-6">                        <select id="catOptionsBox" name="cat" class="form-control">                            <option value="open">Open</option>                            <option value="closed">Closed</option>                            <option value="mixed">Mix</option>                        </select>                    </div>                </div>                <div class="form-group" id="subCatBox" style="display: none;">                    <label class="control-label col-lg-2">Sub-Category</label>                    <div class="col-lg-6">                        <select id="subCatOptionsBox" name="type" class="form-control"></select>                    </div>                </div>                <hr>                <div id="optionsContainer">&nbsp;</div>                <div class="form-group">                    <div class="col-lg-6">                        <input type="hidden" value="<?php echo $surveyId;?>" name="surveyId">                        <button type="submit" name="submit" value="submit" class="btn bg-teal-400">Submit<i class="icon-arrow-right14 position-right"></i></button>                        <button type="submit" name="addNew" value="addNew" class="btn bg-teal-400">Submit & Add Question<i class="icon-arrow-right14 position-right"></i></button>                        <a href="<?php echo base_url('admin/surveys')?>">                            <button type="button" class="btn bg-teal-400">Cancel<i class="icon-arrow-right14 position-right"></i></button>                        </a>                    </div>                </div>            </fieldset>        </form>    </div></div><script>    function closed(){        var html = '';        html += '<option data-url="closed_dic" value="Single-Dicotomics">Single Dicotomics</option>';        html += '<option data-url="closed_pol" value="Single-Polytomics">Single Polytomics</option>';        html += '<option data-url="closed_pol" value="Multiple-Choice">Multiple Choice</option>';        html += '<option data-url="closed_pol" value="Ranking">Ranking</option>';        html += '<option data-url="scale_numeric" value="Scale-Numeric">Scale Numeric</option>';        html += '<option data-url="closed_pol" value="Scale-Nominal">Scale Nominal</option>';        html += '<option data-url="closed_pol" value="Likert">Likert</option>';        return html;    }    function mixed(){        var html = '';        html += '<option data-url="closed_dic" value="Single-Dicotomics">Single Dicotomics</option>';        html += '<option data-url="open" value="Open">Open</option>';        html += '<option data-url="closed_pol" value="Single-Polytomics">Single Polytomics</option>';        html += '<option data-url="closed_pol" value="Multiple-Choice">Multiple Choice</option>';        html += '<option data-url="closed_pol" value="Ranking">Ranking</option>';        html += '<option data-url="scale_numeric" value="Scale-Numeric">Scale Numeric</option>';        html += '<option data-url="closed_pol" value="Scale-Nominal">Scale Nominal</option>';        html += '<option data-url="closed_pol" value="Likert">Likert</option>';        return html;    }    function open(){        $("#subCatBox").hide(200);        $("#optionsContainer").html(' ');        return false;    }    function closed_dic(){        var html = '';        html += '<div class="form-group optionsSet">';        html += '<label class="control-label col-lg-2">Option 1: </label>';        html += '<div class="col-lg-6">';        html += '<input type="text" required="" name="options[]" value="" class="form-control">';        html += '</div>';        html += '</div>';        html += '<div class="form-group optionsSet">';        html += '<label class="control-label col-lg-2">Option 2: </label>';        html += '<div class="col-lg-6">';        html += '<input type="text" required="" name="options[]" value="" class="form-control">';        html += '</div>';        html += '</div>';        return html;    }    function closed_pol(){        var html = '';        html += '<div class="form-group optionsSet">';        html += '<label class="control-label col-lg-2">Option 1: </label>';        html += '<div class="col-lg-6">';        html += '<input type="text" required="" name="options[]" value="" class="form-control">';        html += '</div>';        html += '</div>';        html += '<div class="form-group optionsSet">';        html += '<label class="control-label col-lg-2">Option 2: </label>';        html += '<div class="col-lg-6">';        html += '<input type="text" required="" name="options[]" value="" class="form-control">';        html += '</div>';        html += '<div class="col-lg-4">';        html += '<button style="margin-right: 10px;" type="button" value="submit" class="addRow btn bg-teal-400">Add</button>';        //html += '<button type="button" value="submit" class="btn bg-teal-400">Remove</button>';        html += '</div>';        html += '</div>';        return html;    }    function scale_numeric(){        var html = '';        html += '<div class="form-group optionsSet">';        html += '<label class="control-label col-lg-2">Max limit: </label>';        html += '<div class="col-lg-6">';        html += '<input type="text" placeholder="App will display numbers upto max limit" required="" name="options[]" value="" class="form-control">';        html += '</div>';        html += '</div>';        return html;    }    function addRow(count){        var html = '';        html += '<div class="form-group optionsSet">';        html += '<label class="control-label col-lg-2">Option '+(count+1)+': </label>';        html += '<div class="col-lg-6">';        html += '<input type="text" required="" name="options[]" value="" class="form-control">';        html += '</div>';        html += '<div class="col-lg-4">';        html += '<button style="margin-right: 10px;" type="button" value="submit" class="addRow btn bg-teal-400">Add</button>';        html += '<button type="button" value="submit" class="removeRow btn bg-teal-400">Remove</button>';        html += '</div>';        html += '</div>';        return html;    }    function writeData(value){        var res = eval(value+"()");        $("#optionsContainer").html(res);        return false;    }    $(document).ready(function() {        $(document).on('click',".addRow",function () {            var count = $("#optionsContainer .form-group").length;            $("#optionsContainer").append(addRow(count));        });        $(document).on('click',".removeRow",function () {            var i =1;            $(this).parents('.form-group').remove();            $('#optionsContainer .form-group').each(function (key,val) {                var obj = $(this);                obj.find(".control-label").html("Option "+i);                i++;            });        });        $("#catOptionsBox").change(function () {           var value = $(this).val();           $("#subCatBox").show(500);           var res = eval(value+"()");           $("#subCatOptionsBox").html(res);           var subCatVal = $('#subCatOptionsBox option:first').prop('selected', true).attr('data-url');            writeData(subCatVal);        });        $("#subCatOptionsBox").change(function (){            var value = $(this).find('option:selected').attr('data-url');            writeData(value);        });        $("input:radio").change(function () {            if($( "#multi" ).is( ":hidden" )) {                //alert(1)                $('#multi').find(':input').prop('disabled', false);                $("#multi").toggle(300);            }else{                //alert(2)                $('#multi').find(':input').prop('disabled', true);                $("#multi").toggle(300);            }        });    });</script><!-- /CKEditor default -->    <!--<div class="form-group">        <label class="control-label col-lg-2">Type: </label>        <div class="col-lg-10">            <label class="radio-inline">                <input type="radio" name="type" value="2" id="singleOption">                Open            </label>            <label class="radio-inline">                <input type="radio" checked="checked" name="type" value="1">                Single Dicotomics            </label>            <label class="radio-inline">                <input type="radio" name="type" value="2" id="singleOption">                Single Politomics            </label>            <label class="radio-inline">                <input type="radio" name="type" value="2" id="singleOption">                Multiple            </label>            <label class="radio-inline">                <input type="radio" name="type" value="2" id="singleOption">                Ranking            </label>            <label class="radio-inline">                <input type="radio" name="type" value="2" id="singleOption">                Scaling            </label>        </div>    </div>-->