selected', this);            var id = elm.val();            var url = '<?php echo base_url('admin/users/getCities');?>';            $.ajax(                {                    url: url,                    type: "POST",                    dataType: "text",                    data: {id:id},                    success: function (data) {                        if (data != '') {                            //alert(JSON.stringify(data));                            $("#loaderImg").hide();                            $('#city').html(data);                        }                        //data: return data from server                    },                    error: function (jqXHR, textStatus, errorThrown) {                        $("#loaderImg").hide();                        alert("Something went wrong... Please try again")                        //if fails                    }                });        });        var password = document.getElementById("password")            , confirm_password = document.getElementById("Cpassword");        function validatePassword(){            if(password.value != confirm_password.value) {                confirm_password.setCustomValidity("Passwords Don't Match");            } else {                confirm_password.setCustomValidity('');            }        }        password.onchange = validatePassword;        confirm_password.onkeyup = validatePassword;    });</script><!-- /CKEditor default -->