<!-- Content area --><div class="content"><!-- CKEditor default --><div class="panel panel-flat">    <div class="panel-heading">        <h5 class="panel-title">Edit Employee Detail</h5>    </div>    <div class="panel-body">        <form method="post" class="form-horizontal" action="<?php echo(base_url('admin/employees/employeeEdit/'.$content['id'])); ?>" enctype="multipart/form-data">            <fieldset class="content-group">                <!--<legend class="text-bold">Add Team Detail</legend>-->                <div class="form-group">                    <label class="control-label col-lg-2">Name: </label>                    <div class="col-lg-6">                        <?php echo form_error('name'); ?>                        <input type="text" required="" name="name" value="<?php echo $content['name']; ?>" class="form-control">                    </div>                </div>                <div class="form-group">                    <label class="control-label col-lg-2">Email: </label>                    <div class="col-lg-6">                        <?php echo form_error('email'); ?>                        <input type="email" readonly required="" name="email" value="<?php echo $content['email']; ?>" class="form-control">                    </div>                </div>                <div class="form-group">                    <label class="control-label col-lg-2">Password: </label>                    <div class="col-lg-6">                        <input type="password" id="password" placeholder="Leave empty if you don't want to change" name="password" value="" class="form-control">                    </div>                </div>                <div class="form-group">                    <label class="control-label col-lg-2">Confirm Password: </label>                    <div class="col-lg-6">                        <input type="password" id="Cpassword" name="Cpassword" value="" class="form-control">                    </div>                </div>                <div class="form-group">                    <div class="col-lg-3">                        <button type="submit" class="btn bg-teal-400">Submit<i class="icon-arrow-right14 position-right"></i></button>                        <a href="<?php echo base_url('admin/employees')?>">                            <button type="button" class="btn bg-teal-400">Cancel<i class="icon-arrow-right14 position-right"></i></button>                        </a>                    </div>                </div>            </fieldset>        </form>    </div></div><script>    $(document).ready(function(){        var password = document.getElementById("password")            , confirm_password = document.getElementById("Cpassword");        function validatePassword(){            if(password.value != confirm_password.value) {                confirm_password.setCustomValidity("Passwords Don't Match");            } else {                confirm_password.setCustomValidity('');            }        }        password.onchange = validatePassword;        confirm_password.onkeyup = validatePassword;    });</script><!-- /CKEditor default -->