v class="form-group" id="city">                    <label class="control-label col-lg-2">Cities:</label>                    <div class="col-lg-6">                        <select id="cities" name="city" class="form-control">                            <?php foreach ($cities as $v) {?>                                <option <?php if($content['city'] == $v['city']) echo 'selected';?> value="<?php echo $v['city'];?>"><?php echo $v['city'];?></option>                            <?php }?>                        </select>                    </div>                </div>                <div class="form-group">                    <label class="control-label col-lg-2">Password: </label>                    <div class="col-lg-6">                        <?php echo form_error('password'); ?>                        <input type="password" id="password" name="password" value="" class="form-control">                    </div>                </div>                <div class="form-group">                    <label class="control-label col-lg-2">Confirm Password: </label>                    <div class="col-lg-6">                        <?php echo form_error('Cpassword'); ?>                        <input type="password" id="Cpassword" name="Cpassword" value="" class="form-control">                    </div>                </div>                <div class="form-group">                    <label class="control-label col-lg-2"> Change Profile Image: </label>                    <div class="col-lg-6">                        <input type="file" name="image" id="image" class="form-control">                    </div>                </div>                <div class="form-group">                    <div class="col-lg-3">                        <button type="submit" class="btn bg-teal-400">Submit<i class="icon-arrow-right14 position-right"></i></button>                        <a href="<?php echo base_url('admin/users')?>">                            <button type="button" class="btn bg-teal-400">Cancel<i class="icon-arrow-right14 position-right"></i></button>                        </a>                    </div>                </div>            </fieldset>        </form>    </div></div><script>    $(document).ready(function(){        $(".workImages").click(function(){           var id = $(this).attr('data-key');                $("#loaderImg").show();                var url = '<?php echo base_url('admin/users/getDeleteWorkImg');?>';                $.ajax(                    {                        url: url,                        type: "POST",                        dataType: "text",                        data: {id:id},                        success: function (data) {                            if (data != '') {                                //alert(JSON.stringify(data));                                $("#loaderImg").hide();                                $('#key-'+id).remove();                                alert(data);                            }                            //data: return data from server                        },                        error: function (jqXHR, textStatus, errorThrown) {                            $("#loaderImg").hide();                            alert("Something went wrong... Please try again")                            //if fails                        }                    });        });        $('#states').on('change', function() {            $("#loaderImg").show();            var elm = $('option:selected', this);            var id = elm.val();            var url = '<?php echo base_url('admin/users/getCities');?>';            $.ajax(                {                    url: url,                    type: "POST",                    dataType: "text",                    data: {id:id},                    success: function (data) {                        if (data != '') {                            //alert(JSON.stringify(data));                            $("#loaderImg").hide();                            $('#cities').html(data);                        }                        //data: return data from server                    },                    error: function (jqXHR, textStatus, errorThrown) {                        $("#loaderImg").hide();                        alert("Something went wrong... Please try again")                        //if fails                    }                });        });        var password = document.getElementById("password")            , confirm_password = document.getElementById("Cpassword");        function validatePassword(){            if(password.value != confirm_password.value) {                confirm_password.setCustomValidity("Passwords Don't Match");            } else {                confirm_password.setCustomValidity('');            }        }        password.onchange = validatePassword;        confirm_password.onkeyup = validatePassword;    });</script><!-- /CKEditor default -->